#include <iostream>
#include <libusb-1.0/libusb.h>
#include <string.h>
#include <unistd.h>

#include <cstdlib>

using namespace std;


int main() {
	libusb_device **devs; //pointer to pointer of device, used to retrieve a list of devices
	libusb_device_handle *dev_handle; //a device handle
	libusb_context *ctx = NULL; //a libusb session
	char buffer[512];
	int r; //for return values
	ssize_t cnt; //holding number of devices in list

	strcpy(buffer, "What is your name ?");
	r = libusb_init(&ctx); //initialize the library for the session we just declared
	if(r < 0) {
		cout<<"Init Error "<<r<<endl; //there was an error
		return 1;
	}
	libusb_set_debug(ctx, 3); //set verbosity level to 3, as suggested in the documentation

	cnt = libusb_get_device_list(ctx, &devs); //get the list of devices
	if(cnt < 0) {
		cout<<"Get Device Error"<<endl; //there was an error
		return 1;
	}
	cout<<cnt<<" Devices in list."<<endl;

	dev_handle = libusb_open_device_with_vid_pid(ctx, 0x00aa, 0x00bb); //these are vendorID and productID I found for my usb device
	if(dev_handle == NULL)
		cout<<"Cannot open device"<<endl;
	else
		cout<<"Device Opened"<<endl;
	libusb_free_device_list(devs, 1); //free the list, unref the devices in it

	char *data = new char[4194304]; //data to write
	{
		int i;
		for(i=0;i<4186112;i++)
		data[i+8192] =0x86;
		data[1048576] = 0x89;
		data[1048575] = 0x89;
	}
	data[0]='a';data[1]='b';data[2]='c';data[3]='d'; //some dummy values
    strcpy(data, "");

	int actual; //used to find out how many bytes were written
	if(libusb_kernel_driver_active(dev_handle, 0) == 1) { //find out if kernel driver is attached 
		cout<<"Kernel Driver Active"<<endl;
		if(libusb_detach_kernel_driver(dev_handle, 0) == 0) //detach it
			cout<<"Kernel Driver Detached!"<<endl;
	}
	r = libusb_claim_interface(dev_handle, 0); //claim interface 0 (the first) of device (mine had jsut 1)
	if(r < 0) {
		cout<<"Cannot Claim Interface"<<endl;
		return 1;
	}
	cout<<"Claimed Interface"<<endl;
	
	cout<<"Writing Data..."<<endl;
while(1) {
	r = libusb_bulk_transfer(dev_handle, (1 | LIBUSB_ENDPOINT_OUT), (unsigned char *)data, 1382400, &actual, 10000);
	cout<<"Writing Data...actualwrite=%d"<<actual<<endl;
usleep(3000);
	switch(r) {
	case LIBUSB_ERROR_TIMEOUT:
				cout<<"LIBUSB_ERROR_TIMEOUT\n"<<endl;
				break;
	case LIBUSB_ERROR_PIPE:
				cout<<"LIBUSB_ERROR_PIPE\n"<<endl;
				break;
	case LIBUSB_ERROR_OVERFLOW:
				cout<<"LIBUSB_ERROR_OVERFLOW\n"<<endl;
				break;
	case LIBUSB_ERROR_NO_DEVICE:
				cout<<"LIBUSB_ERROR_NO_DEVICE\n"<<endl;
				break;
	case 0:
		cout<<"WRITE_SUCCESS"<<endl;
	default:
		break;
	}
	r = libusb_bulk_transfer(dev_handle, (0x81 | LIBUSB_ENDPOINT_IN), (unsigned char *)data, 1382400, &actual, 10000); 
usleep(3000);
	cout<<"Writing Data...actualread=%d"<<actual<<endl;
	switch(r) {
	case LIBUSB_ERROR_TIMEOUT:
				cout<<"LIBUSB_ERROR_TIMEOUT\n"<<endl;
				break;
	case LIBUSB_ERROR_PIPE:
				cout<<"LIBUSB_ERROR_PIPE\n"<<endl;
				break;
	case LIBUSB_ERROR_OVERFLOW:
				cout<<"LIBUSB_ERROR_OVERFLOW\n"<<endl;
				break;
	case LIBUSB_ERROR_NO_DEVICE:
				cout<<"LIBUSB_ERROR_NO_DEVICE\n"<<endl;
				break;
	case 0:
		cout<<"READ_SUCCESS"<<endl;
	default:
		break;
	}
}
	if(r == 0 && actual == 512) 
		cout<<"Writing Successful!"<<endl;
	else
		cout<<"Write Error"<<endl;
	
	r = libusb_release_interface(dev_handle, 0); //release the claimed interface
	if(r!=0) {
		cout<<"Cannot Release Interface"<<endl;
		return 1;
	}
	cout<<"Released Interface"<<endl;

	libusb_close(dev_handle); //close the device we opened
while(1);
	libusb_exit(ctx); //needs to be called to end the

	delete[] data; //delete the allocated memory for data
	return 0;
}
